import React, { useState } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
const Tab = (props) => {
  const [content, setContent] = useState('')
  const handleClick = (e) => {
    setContent(`Tab ${props.idx}content is showing here`)
  }
  return (
    <div>
      <h3 className="btn btn-outline-dark" onClick={handleClick}>
        Tab {props.idx}
      </h3>
      <div className="container border my-5 bg-light">
        <p>{content}</p>
      </div>
    </div>
  )
}

export default Tab
